def pdf_build():
    executable_path = 'node_modules/phantomjs-prebuilt/lib/phantomjs.js'

    #driver = webdriver.PhantomJS(executable_path=executable_path, service_log_path='/tmp/ghostdriver.log')
    #driver.set_window_size(1024, 768) # optional
    #driver.get('https://google.com/')
    #driver.save_screenshot('/tmp/screen.png')

    def download(driver, target_path):
        def execute(script, args):
            driver.execute(executable_path, {'script': script, 'args': args})
            
            driver.command_executor._commands[executable_path] = ('POST', '/session/$sessionId/phantom/execute')
            page_format = 'this.paperSize = {format: "A4", orientation: "portrait" };'
            execute(page_format, [])
            render = '''this.render("{}")'''.format(target_path)
            execute(render, [])

    driver = webdriver.PhantomJS(executable_path='node_modules/phantomjs-prebuilt/lib/phantom/bin/phantomjs')
    driver.get('http://stackoverflow.com')
    download(driver, "/tmp/save_me.pdf")
